// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract AbusiveStorage {
    address public admin;
    mapping(address => uint256) public userStorage;
    uint256 public storageLimit = 1000;

    event StorageUsed(address indexed user, uint256 amount);
    event AbusiveStorageDetected(address indexed user, uint256 amount);

    constructor() {
        admin = msg.sender;
    }

    function storeData(uint256 amount) public {
        userStorage[msg.sender] += amount;
        emit StorageUsed(msg.sender, amount);

        if (userStorage[msg.sender] > storageLimit) {
            emit AbusiveStorageDetected(msg.sender, userStorage[msg.sender]);
        }
    }

    function setStorageLimit(uint256 newLimit) public {
        require(msg.sender == admin, "Only admin can set limit");
        storageLimit = newLimit;
    }

    function getUserStorage(address user) public view returns (uint256) {
        return userStorage[user];
    }
}
